import React, { useState, useEffect, useRef } from 'react';
import './Navbar.css'; // Importar o arquivo CSS para estilização

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const menuRef = useRef(null); // Referência ao menu

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  // Fecha o menu se clicar fora dele
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="logo">Nery</div>
      <button className={`menu-toggle ${isOpen ? 'active' : ''}`} onClick={toggleMenu} aria-label="Toggle navigation">
        ☰
      </button>
      <ul ref={menuRef} className={`nav-menu ${isOpen ? 'active' : ''}`}>
        <li><a href="#about">About Me</a></li>
        <li><a href="#hobbys">Hobbys</a></li>
        <li><a href="#work-experience">Work Experience</a></li>
        <li><a href="#dev-skills">Dev Skills</a></li>
        <li><a href="#contact">Contact Me</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
